import React from "react";
import {Link} from "react-router-dom";
import styled from "styled-components";

import { COLORS } from "../Constants";
import { FiInstagram, FiFacebook } from "react-icons/fi";
import { GoLocation } from "react-icons/go";

const Footer = () => {
  return (
    <>
      <Wrapper>       
          <Hours>
            <P>Hours</P>
            <ul>
              <li>Sun-Mon CLOSED</li>
              <li>Tue-Sat 12:00-21:00</li>             
            </ul>
          </Hours>
          <Medias>
              <A href="https://www.instagram.com/restaurantstashcafe/"><FiInstagram /></A>
          <A href="https://www.facebook.com/stashcafemtl/"><FiFacebook /></A>
            <Extension to="/contact">
            <GoLocation />
            </Extension>
          </Medias>       
      </Wrapper>
    </>
  );
};

export default Footer;

const Wrapper = styled.footer`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  color: ${COLORS.red};
  font-size: 18px;

    margin-top:20px;
  @media (max-width:769px){
    flex-direction: column-reverse;
    justify-content:center;
    align-items:center;
    margin-top:20px;
}
  
  `;

const P = styled.p`
  font-weight: bold;
`;
const Hours = styled.div`
  display: flex;
  flex-direction: column;
  @media (max-width:769px){
   margin:15px;
    justify-content:center;
    align-items:center;
    margin-bottom:25px;
}

`;

const A = styled.a`
  color: ${COLORS.red};
`;
const Medias = styled.div`
  display: flex;
  flex-direction: row;
`;

const Extension = styled(Link)`
color:${COLORS.red}
`;
