import React, {useState} from "react";
import {Link} from "react-router-dom";
import styled from "styled-components";

import { COLORS } from "../Constants";

const ContactForm = ()=>{
    const [name, setName] = useState(null);
    const [telNumber, setTelNumber] = useState(null);
    const [mail, setMail] = useState(null);
    const [subject, setSubject] = useState(null);
    const [mailContent, setMailContent] = useState(null);

    const mailManager = ()=>{
    };

    return(
        <>
        <Wrapper>
        <Form
        onSubmit={(ev)=>{
        ev.preventDefault();
        mailManager();
}}>
    <Label>Name*</Label>
       <Input
        type="name"
        value={name}
        onChange={(ev) => setName(ev.target.value)}/>
    <Label>Phone Number*</Label>
       <Input
        type="tel"
        value={telNumber}
        onChange={(ev) => setTelNumber(ev.target.value)}/>
    <Label>Email*</Label>
       <Input
        type="mail"
        value={mail}
        onChange={(ev) => setMail(ev.target.value)}/>
    <Label>Subject</Label>
       <Input
        type="subject"
        value={subject}
        onChange={(ev) => setSubject(ev.target.value)}/>
       <TextArea
        placeholder="I will be happy to asnwer any of your questions"
        value={mailContent}
        onChange={(ev) => setMailContent(ev.target.value)}
        />
        <Button type="submit">send</Button>
</Form>
        </Wrapper>
        </>
    )
}

export default ContactForm;


const Wrapper = styled.div``;
const Form = styled.form``;
const Label = styled.label``;
const Input = styled.input``;
const TextArea = styled.textarea``;
const Button = styled.button``;
