import React, {useState} from "react";
import {Link} from "react-router-dom";
import styled from "styled-components";

import { COLORS } from "../Constants";

const ContactForm = ()=>{
    const [name, setName] = useState(null);
    const [telNumber, setTelNumber] = useState(null);
    const [mail, setMail] = useState(null);
    const [subject, setSubject] = useState(null);
    const [mailContent, setMailContent] = useState(null);

    const mailManager = ()=>{
    };

    return(
        <>
        <Wrapper>
        <Form
        onSubmit={(ev)=>{
        ev.preventDefault();
        mailManager();
}}>
    <Small>
    <Label>Name*</Label>
       <Input
        type="name"
        value={name}
        onChange={(ev) => setName(ev.target.value)}/>
    <Label>Phone Number*</Label>
       <Input
        type="tel"
        value={telNumber}
        onChange={(ev) => setTelNumber(ev.target.value)}/>
    <Label>Email*</Label>
       <Input
        type="mail"
        value={mail}
        onChange={(ev) => setMail(ev.target.value)}/>
    <Label>Subject</Label>
       <Input
        type="subject"
        value={subject}
        onChange={(ev) => setSubject(ev.target.value)}/>
        </Small>
        <Big>
       <TextArea
        value={mailContent}
        onChange={(ev) => setMailContent(ev.target.value)}
        />
        <Button type="submit">SUBMIT</Button>
        </Big>
</Form>
        </Wrapper>
        </>
    )
}

export default ContactForm;


const Wrapper = styled.div`
margin-top:25px;
color: ${COLORS.red};
display:flex;
align-items:center;
justify-content:center;
`;
const Small = styled.div``;
const Big = styled.div`
margin:15px;
display:flex;
align-items:flex-start;
justify-content:flex-start;
flex-direction:column;
`;
const Form = styled.form``;
const Label = styled.label``;
const Input = styled.input`
  padding: 5px;
  margin: 3px;
  border: 0;
  width: 200px;

  border-bottom: solid 2px ${COLORS.red};
  box-shadow: 0 0 10px 2px rgba(0, 0, 0, 0.06);

  outline: none;
`;
const TextArea = styled.textarea`
height: 200px;
margin-top:25px;
resize: none;
width: 1100px;
padding: 5px;
margin: 3px;
border: 0;
border-bottom: solid 2px ${COLORS.red};
box-shadow: 0 0 10px 2px rgba(0, 0, 0, 0.06);
outline: none;
`;
const Button = styled.button`
margin-top:25px;
cursor: pointer;
border-radius:3px;
padding:25px;
font-weight:bold;
color: ${COLORS.red};
width: 200px;
font-size: 15px;
background:none;
border: solid ${COLORS.red} 3px;
transition-property: all;
transition-duration: 0.5s;
transition-timing-function: ease-in;
:hover{
  background-color:#FFFFFF;
font-size: 14px;
  color:${COLORS.red};
}
`;
