import React, {useState} from "react";
import styled from "styled-components";

import {FiSend} from "react-icons/fi"

import { COLORS } from "../Constants";

const ContactForm = ()=>{
    const [name, setName] = useState(null);
    const [telNumber, setTelNumber] = useState(null);
    const [mail, setMail] = useState(null);
    const [subject, setSubject] = useState(null);
    const [mailContent, setMailContent] = useState(null);

    const mailManager = ()=>{
      const mailBody ={
        name,
        telNumber,
        mail,
        subject,
        mailContent
            };
      fetch("/contactForm", {
        headers:{"Content-Type":"application/json"},
        method:"POST",
        body: JSON.stringify(mailBody),
      })
      .then((res)=>res.json())
      .then((data)=>console.log("form Answer",data))
    };

    return(
        <>
        <Wrapper>
        <Form
        method="POST"
        action="/"
        onSubmit={(ev)=>{
        ev.preventDefault();
        mailManager();
}}>
    <Small>
     
    <Label>Name*</Label>
       <Input
       required
        type="name"
        value={name}
        onChange={(ev) => setName(ev.target.value)}/>
      
    <Label>Phone Number</Label>
       <Input
        type="tel"
        value={telNumber}
        onChange={(ev) => setTelNumber(ev.target.value)}/>
    <Label>Email*</Label>
       <Input
       required
        type="mail"
        value={mail}
        onChange={(ev) => setMail(ev.target.value)}/>
    <Label>Subject*</Label>
       <Input
       required
        type="subject"
        value={subject}
        onChange={(ev) => setSubject(ev.target.value)}/>
        </Small>
        <Big>
        <Label>Your Message</Label>
       <TextArea
        value={mailContent}
        onChange={(ev) => setMailContent(ev.target.value)}
        />
        <Button type="submit"><FiSendMess/></Button>
        </Big>
</Form>
        </Wrapper>
        </>
    )
}

export default ContactForm;


const Wrapper = styled.div`
border: solid red 1px;
margin-top:25px;
margin-bottom:25px;
color: ${COLORS.red};
/* display:flex;
align-items:center;
justify-content:center; */

`;
const Small = styled.div`
border: solid black 1px;
margin: 25px;
display:flex;
flex-direction:row;
flex-wrap:wrap;
@media (max-width:769px){
  width:100%;
  background-color:white;
  display:flex;
flex-direction:column;
}
`;

const Big = styled.div`
border: solid orange 1px;
margin:15px;
display:flex;
align-items:flex-start;
justify-content:flex-start;
flex-direction:column;
@media (max-width:769px){
  display:flex;
flex-wrap:wrap;
align-items:center;
justify-content:center;

}

flex-wrap:wrap;
`;
const Form = styled.form`
border: solid black 1px;
display:flex;
flex-wrap:wrap;
align-items:center;
justify-content:center;
`;
const Label = styled.label`
border: solid black 1px;
display:flex;
flex-direction:row;
flex-wrap:wrap;
`;
const Input = styled.input`

  padding: 5px;
  margin: 3px;
  border: solid blue 2px;
  width: 200px;
  border-bottom: solid 2px ${COLORS.black};
  /* box-shadow: 0 0 10px 2px rgba(0, 0, 0, 0.06); */
