import React, {useEffect} from "react";
import { COLORS } from "../Constants";
import styled from "styled-components";

import Fade from "react-reveal/Fade";
import lawa from "../Assets/lawa.JPG";
import Footer from "./Footer";

//IDEA: MAKE A PICTURE CAROUSEL IN HOMEPAGE (YOU WILL GET THE VIBE OF THE PLACE)

const HomePage = () => {
  useEffect(() => {
    fetch("/home")
      .then((res) => res.json())
      .then((data) => {
        console.log("data", data);
     
      });
  }, []);
  return (
    <>
      <Wrapper>
        <Fade>
          <Button>TAKE OUT</Button>
          {/* <HeadPic src={lawa} alt="restaurant panoramic view" /> */}
        </Fade>
  
      </Wrapper>
    </>
  );
};

export default HomePage;

const Title = styled.h1`
  /* font-size: 50px;
  color: ${COLORS.red};
  font-family: "Playfair Display", serif; */

  margin: 15px;
`;

const Wrapper = styled.div`
  font-size: 25px;
  color: black;
  background-image: url(${lawa});
  width: 100%;
  height: 650px;
  object-fit: cover;
  display: flex;
  justify-content: center;
  align-items: center;
  

`;

const HeadPic = styled.img`
  width: 100%;
  height: 650px;
  object-fit: cover;
`;

const Button = styled.button`
border: none;
width: 300px;
font-size: 30px;
background:none;
`;

const Img = styled.img`
  border-radius: 3px;
  margin: 20px;
  width: 300px;
`;
