import React, {useState} from "react";
import {GoogleMap, Marker, withScriptjs, withGoogleMap, DirectionsRenderer} from "react-google-maps";
import { COLORS } from "../Constants";
import styled from "styled-components";

function Map(){
const [name, setName] = useState(null);

  const mailManager = ()=>{

  
  };
 
  return(
<GoogleMap defaultZoom={19} defaultCenter={{lat:45.502651,lng:-73.555252}}>
  <Marker position={{lat:45.502651,lng:-73.555252}}/>
  </GoogleMap>
  )
}

const WrappedMap = withScriptjs(withGoogleMap(Map));


const Contact = () => {



// API:  AIzaSyCVlMOHxbX3_vH60KHLx47_8IuO8MSDU00
  return (
    <>
    <ContactForm>
<Form
onSubmit={(ev)=>{
  ev.preventDefault();
  mailManager();
  
}}>
       <Input
                    type="subject"
                    placeholder="subject"
                    value={name}
                    onChange={(ev) => setName(ev.target.value)}
                  />
                  <Button type="submit">send</Button>
</Form>
    </ContactForm>
      <MapAddress>

        <MapDiv>

          <WrappedMap googleMapURL={`https://maps.googleapis.com/maps/api/js?key=AIzaSyCVlMOHxbX3_vH60KHLx47_8IuO8MSDU00&callback=initMap`}
            loadingElement={<div style={{ height: `100%` }} />}
            containerElement={<div style={{ height: `400px` }} />}
            mapElement={<div style={{ height: `100%` }} />}
          />
        </MapDiv>
          <AddressBox>
            <Ul>
              <Li>ADRESS</Li>
              <Li>200 rue st paul O</Li>
              <Li>Montreal, QC, H3S1T8</Li>
              <Li>metro place d'arme</Li>
              <Li>(514)845.6611</Li>
              <Li><Button>GET DIRECTIONS</Button></Li>
            </Ul>
          </AddressBox>
      
      </MapAddress>
    </>
  );
};

export default Contact;

const ContactForm = styled.div``;
const Form = styled.form``;
const Input = styled.input``;
const MapAddress = styled.div`
  font-size: 55px;
  color: ${COLORS.red};
  display:flex;
  flex-direction:row;
  padding: 25px;
  flex-wrap:wrap;
  justify-content:space-around;
`;

const MapDiv = styled.div`
border: solid 1px black;
width:1000px;


`;

const AddressBox = styled.div`
font-size:25px;
display:flex;
flex-direction:column;

`;

const Ul = styled.ul``;
const Li = styled.li`
margin:20px;
`;
const Button = styled.button`
cursor: pointer;
border-radius:3px;
padding:25px;
font-weight:bold;
color: ${COLORS.red};
width: 220px;
font-size: 15px;
background:none;
border: solid ${COLORS.red} 3px;
transition-property: all;
  transition-duration: 0.5s;
  transition-timing-function: ease-in;
:hover{
  background-color:#FFFFFF;
font-size: 14px;
  color:${COLORS.red};
  


  
}`;
